sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

    if (isLoading || newValue == '') {

        return;

    }

    // Get the email of the caller by dot-walking

    var callerEmail = g_form.getReference('requestor_name', function(caller) {

        if (caller) {

            // Set the email field with the caller's email address

            //g_form.setValue('pgs_name', caller.x_1226426_archive_pgs_name);
			g_form.setValue('region_country', caller.x_1226426_archive_country);
			g_form.setValue('project_manager',caller.x_1226426_archive_project_manager)

        } else {

            // Clear the email field if no caller is found

            //g_form.clearValue('pgs_name');
			g_form.clearValue('region_country');
			g_form.clearValue('project_manager');

        }

    });

}
